const int MOD = (int)1e9 + 9;
const int kN = 250000 + 5;
using LL = long long;
int n;
char s[kN];
const std::vector<int> primes{(int)1e9 + 7, (int)1e8 + 7, 233333, (int)1e6 + 7, 13};
const int kSize = primes.size();
std::vector<int> inv_primes(kSize);


int inv(int x) {
  return x == 1 ? 1 : (MOD - MOD / x) * 1LL * inv(MOD % x) % MOD;
}

std::vector<int> calc(const int *a, const int *b, const int *c) {
  std::vector<int> ret(kSize);
  for (int i = 0; i < kSize; ++ i)
    ret[i] = (a[i] * 1LL * c[i] % MOD + b[i]) % MOD;
  return ret;
}

bool operator < (const std::vector<int>& lhs, const std::vector<int> &rhs) {
  if (lhs.size() != rhs.size())
    return lhs.size() < rhs.size();
  for (int i = 0; i < lhs.size(); ++ i) {
    if (lhs[i] < rhs[i])
      return true;
    else if (lhs[i] > rhs[i])
      return false;
  }
  return false;
}

LL solve() {
  for (int i = 0; i < kSize; ++ i)
    inv_primes[i] = inv(primes[i]);
  LL ret = 0;
  std::map<std::vector<int>, int> map;
  map[std::vector<int>(kSize, 0)] = 1;
  int c[kSize] = {};
  for (int i = n; i >= 1; -- i) {
    char ch = s[i - 1];
    for (int j = 0; j < kSize; ++ j) {
      if (ch == '+')
        c[j] = (c[j] + 1) % MOD;
      else if (ch == '-')
        c[j] = (c[j] + MOD - 1) % MOD;
      else if (ch == '>')
        c[j] = c[j] * 1LL * primes[j] % MOD;
      else
        c[j] = c[j] * 1LL * inv_primes[j] % MOD;
    }
  }
  int a[kSize] = {}, b[kSize] = {};
  for (int i = 0; i < kSize; ++ i)
    a[i] = 1;
  for (int i = n; i >= 1; -- i) {
    char ch = s[i - 1];
    for (int j = 0; j < kSize; ++ j) {
      if (ch == '-')
        b[j] = (b[j] + a[j]) % MOD;
      else if (ch == '+')
        b[j] = (b[j] + MOD - a[j]) % MOD;
      else if (ch == '<')
        a[j] = a[j] * 1LL * primes[j] % MOD;
      else
        a[j] = a[j] * 1LL * inv_primes[j] % MOD;
    }
    auto it = map.find(calc(a, b, c));
    if (it != map.end())
      ret += it->second;
    map[std::vector<int>(b, b + kSize)] ++;
  }
  return ret;
}

int main() {
  srand(time(NULL));
  scanf("%d%s", &n, s);
  printf("%lld\n", solve());
}

