static const ll modb = 1000000000000091LL;
static const ll pa = 17LL;
static const ll pb = 19LL;

ll powa_raw[520000];
ll powb_raw[520000];

ll inv(ll a, ll p) {
  return (a == 1 ? 1 : (1 - p * inv(p % a, a)) / a + p);
}

void solve(ll N, string S) {
  const __int128 inv_pa = inv(pa, moda);
  const __int128 inv_pb = inv(pb, modb);
  ll *A = powa_raw + 260000;
  ll *B = powb_raw + 260000;
  A[0] = 1; B[0] = 1;
  REP(i,255000) {
    A[i+1] = (A[i] * pa) % moda;
    A[-(i+1)] = (A[-i] * inv_pa) % moda;
    B[i+1] = (B[i] * pb) % modb;
    B[-(i+1)] = (B[-i] * inv_pb) % modb;
  }
  map<pair<ll,ll>,int> cnt;
  cnt[make_pair(0LL, 0LL)] = 1;
  ll suma = 0, sumb = 0, P = 0;
  REP(i,N) {
    if (S[i] == '+') { suma += A[P]; sumb += B[P]; }
    if (S[i] == '-') { suma -= A[P]; sumb -= B[P]; }
    suma = (suma + moda) % moda;
    sumb = (sumb + modb) % modb;
    if (S[i] == '>') ++P;
    if (S[i] == '<') --P;
    cnt[make_pair(suma, sumb)] += 1;
  }
  const ll ta = suma, tb = sumb;
  ll res = 0;
  suma = 0, sumb = 0, P = 0;
  REP(i,N) {
    cnt[make_pair(suma, sumb)] -= 1;
    ll tta = (__int128(ta) * A[P] + suma) % moda;
    ll ttb = (__int128(tb) * B[P] + sumb) % modb;
    res += cnt[make_pair(tta, ttb)];
    if (S[i] == '+') { suma += A[P]; sumb += B[P]; }
    if (S[i] == '-') { suma -= A[P]; sumb -= B[P]; }
    suma = (suma + moda) % moda;
    sumb = (sumb + modb) % modb;
    if (S[i] == '>') ++P;
    if (S[i] == '<') --P;
  }
  cout << res << endl;
}

int main() {
	string S;
	ll N;
	scanf("%lld", &N);
	cin >> S;
	solve(N, S);
	return 0;
}
