const int Maxv = 500010; 
const ull Prime1 = 239; 
const ull Prime2 = 223372036854775763; 

int idx[Maxv], ans, n; 
ull Hash[Maxv], val[Maxv]; 
char s[Maxv]; 

std::unordered_map<ull, int> Map; 

inline void PreHandle() {
    val[n] = 1; 
    for (int i = n + 1; i <= (n << 1); i++)
        val[i] = val[i - 1] * Prime1; 
    for (int i = n - 1; i >= 0; i--)
        val[i] = val[i + 1] * Prime2; 
}

signed main() {
    scanf("%d", &n); 
    scanf("%s", s); 

    PreHandle(); 

    for (int i = 0; i < n; i++) {
        Hash[i + 1] = Hash[i]; 
        idx[i + 1] = idx[i]; 

        if (s[i] == '>')
            idx[i + 1]++; 
        if (s[i] == '<')
            idx[i + 1]--;
        if (s[i] == '+')
            Hash[i + 1] += val[n + idx[i + 1]]; 
        if (s[i] == '-')
            Hash[i + 1] -= val[n + idx[i + 1]]; 
    }

    for (int i = n; i >= 0; i--) {
        ull tmp = Hash[i] + Hash[n] * val[n + idx[i]];
        ans += Map[tmp]; 
        Map[ Hash[i] ]++;
    }

    printf("%lld\n", ans);

    return 0; 
}

#include<bits/stdc++.h>

using namespace std;

#define int long long

const int   N   = 5e5 + 1;
const int   B   = 1e8 + 1;

typedef pair<int,int>   ii;
typedef pair<ii,ii>     pii;

int qpow(int a,int exp,int mod) {
    if(exp < 0) exp = mod - 1 + exp;
    int ans = 1;
    while (exp) {
        if(exp & 1) ans = ans * a % mod;
        a = a * a % mod;  exp >>= 1;
    }
    return ans;
}

void add(int &a,int b,int mod) {
    a += b;
    if(a >= mod)a -= mod;
    if(a < 0)   a += mod;
}

int mod[4];
int n, f[N];
int H[N][4];
string S;

void process(int t)   {
    for(int i = 1 ; i <= n ; ++i)   {
        H[i][t] = H[i - 1][t];
        if(S[i - 1] == '+') add(H[i][t], qpow(B,f[i],mod[t]),mod[t]);
        if(S[i - 1] == '-') add(H[i][t],-qpow(B,f[i],mod[t]),mod[t]);
    }
}

map<pii,int> mp;

signed main()   {
    ios_base::sync_with_stdio(0);
    cin.tie(0); cout.tie(0);

    cin >> n >> S;

    for(int i = 0 ; i < n ; ++i)    {
        f[i + 1] = f[i];
        if(S[i] == '>') f[i + 1]++;
        if(S[i] == '<') f[i + 1]--;
    }

    mod[0] = 1e9 + 7;
    mod[1] = 1e9 + 9;
    mod[2] = 1e9 + 21;
    mod[3] = 1e9 + 33;

    process(0);
    process(1);
    process(2);
    process(3);

    mp[pii({H[n][0],H[n][1]},{H[n][2],H[n][3]})] = 1;

    int ans = 0;

    for(int i = 1 ; i <= n ; ++i)   {
        ans += mp[pii({H[i][0],H[i][1]},{H[i][2],H[i][3]})];
        int res0 = H[n][0] * qpow(B,f[i],mod[0]) + H[i][0];
        int res1 = H[n][1] * qpow(B,f[i],mod[1]) + H[i][1];
        int res2 = H[n][2] * qpow(B,f[i],mod[2]) + H[i][2];
        int res3 = H[n][3] * qpow(B,f[i],mod[3]) + H[i][3];
        res0 %= mod[0]; res1 %= mod[1];
        res2 %= mod[2]; res3 %= mod[3];
        mp[pii({res0,res1},{res2,res3})]++;
    }

    cout << ans << endl;
}

