int powi(int a, int b, int m)
{
    int c = 1;
    for (; b; b >>= 1, a = 1ll * a * a % m)
        if (b & 1) c = 1ll * c * a % m;
    return c;
}
const int BAS = 233;
const int MOD1 = 1e9 + 7;
const int MOD2 = 1e9 + 9;
const int BIV1 = powi(BAS, MOD1 - 2, MOD1);
const int BIV2 = powi(BAS, MOD2 - 2, MOD2);
