    V[0] = make_pair(1, 1);
    for (int i = 1; i <= n; ++ i)
    {
        if (st[i] == '>')
            V[i] = make_pair(1ll * V[i - 1].first * BAS % MOD1, 1ll * V[i - 1].second * BAS % MOD2),
            S[i] = S[i - 1];
        else if (st[i] == '<')
            V[i] = make_pair(1ll * V[i - 1].first * BIV1 % MOD1, 1ll * V[i - 1].second * BIV2 % MOD2),
            S[i] = S[i - 1];
        else if (st[i] == '+')
            V[i] = V[i - 1],
            S[i] = make_pair((S[i - 1].first + V[i].first) % MOD1, (S[i - 1].second + V[i].second) % MOD2);
        else if (st[i] == '-')
            V[i] = V[i - 1],
            S[i] = make_pair((S[i - 1].first - V[i].first + MOD1) % MOD1, (S[i - 1].second - V[i].second + MOD2) % MOD2);
    }
    for (int i = n; ~i; -- i)
    {
        res += C[make_pair((1ll * S[n].first * V[i].first + S[i].first) % MOD1,
            (1ll * S[n].second * V[i].second + S[i].second) % MOD2)];
        C[S[i]] ++;
    }
    printf("%lld\n", res);
}

