        a[k].emplace_back(s[i], k);
        ia[k].emplace_back(a[k][i].inv());
    }
    auto isuf = ia;
    for (int i = n-2; i >= 0; i--) {
        isuf[k][i] = isuf[k][i+1] * isuf[k][i];
    }
    ll c = isuf[k][0].inv().eval(0);
    z[n][k] = 0;
    for (int i = 0; i < n; i++) {
        z[i][k] = isuf[k][i].eval(0);
        g[i][k] = isuf[k][i].eval(c);
    }
    }
    map<Token, int> cnt;
    ll res = 0;
    for (int i = 0; i < n; i++) {
        cnt[g[i]]++;
        res += cnt[z[i+1]];
    }
    cout << res;
}

