#include <bits/stdc++.h>
using namespace std;

const int N = 255000;
const int MOD_CNT = 2;
const int MOD[MOD_CNT] = {(int)1e9 + 7, (int)1e9 + 9};
const int Base = 998244353;

inline int modExp(int a, int n, int mod) {
	int ret = 1;
	while (n) {
		if (n & 1) ret = (long long)ret * a % mod;
		a = (long long)a * a % mod;
		n >>= 1;
	}
	return ret;
}

struct Rab {
	int val[MOD_CNT];
	Rab(long long x = 0, int inv = 0) {
		for (int i = 0; i < MOD_CNT; i++) {
			if (inv) val[i] = modExp(x, MOD[i] - 2, MOD[i]);
			else val[i] = (x % MOD[i] + MOD[i]) % MOD[i];
		}
	}
	Rab operator + (const Rab &d) const {
		Rab ret;
		for (int i = 0; i < MOD_CNT; i++) {
			(ret.val[i] = val[i] + d.val[i]) >= MOD[i] && (ret.val[i] -= MOD[i]);
		}
		return ret;
	}
	Rab operator - (const Rab &d) const {
		Rab ret;
		for (int i = 0; i < MOD_CNT; i++) {
			(ret.val[i] = val[i] - d.val[i]) < 0 && (ret.val[i] += MOD[i]);
		}
		return ret;
	}
	Rab operator * (const Rab &d) const {
		Rab ret;
		for (int i = 0; i < MOD_CNT; i++) {
			ret.val[i] = (long long)val[i] * d.val[i] % MOD[i];
		}
		return ret;
	}
	bool operator == (const Rab &d) const {
		for (int i = 0; i < MOD_CNT; i++) if (val[i] != d.val[i]) return false;
		return true;
	}
	bool operator != (const Rab &d) const {
		for (int i = 0; i < MOD_CNT; i++) if (val[i] != d.val[i]) return true;
		return false;
	}
	bool operator < (const Rab &d) const {
		for (int i = 0; i < MOD_CNT; i++) {
			if (val[i] != d.val[i]) return val[i] < d.val[i];
		}
		return false;
	}
} wrk[N + N], *pw;

char s[N];
map<Rab, int> H;

int main() {

	ios::sync_with_stdio(0);
	cin.tie(0), cout.tie(0);

	int n; cin >> n >> s + 1;
	pw = wrk + N;
	pw[0] = 1;
	for (int i = 1; i < N; i++) pw[i] = pw[i - 1] * Base;
	auto inv = Rab(Base, 1);
	for (int i = -1; i > -N; i--) pw[i] = pw[i + 1] * inv;

	int p = 0;
	Rab final = 0;
	for (int i = 1; i <= n; i++) {
		if (s[i] == '+') final = final + pw[p];
		if (s[i] == '-') final = final - pw[p];
		if (s[i] == '>') p++;
		if (s[i] == '<') p--;
	}

	function<long long(int, int)> calc = [&](int st, int en) {
		if (st == en) {
			Rab cur = 0;
			if (s[st] == '+') cur = cur + pw[0];
			if (s[st] == '-') cur = cur - pw[0];
			if (cur == final) return 1LL;
			else return 0LL;
		}
		int mid = st + en >> 1;
		auto ret = calc(st, mid) + calc(mid + 1, en);
		H.clear();
		int p = 0;
		Rab cur = 0;
		for (int i = mid + 1; i <= en; i++) {
			if (s[i] == '+') cur = cur + pw[p];
			if (s[i] == '-') cur = cur - pw[p];
			if (s[i] == '>') p++;
			if (s[i] == '<') p--;
			H[cur]++;
		}
		cur = 0, p = 0;
		for (int i = mid; i >= st; i--) {
			if (s[i] == '+') cur = cur + pw[0];
			if (s[i] == '-') cur = cur - pw[0];
			if (s[i] == '>') cur = cur * pw[1], p++;
			if (s[i] == '<') cur = cur * pw[-1], p--;
			auto now = (final - cur) * pw[-p];
			if (H.count(now)) ret += H[now];
		}
		return ret;
	};

	cout << calc(1, n) << "\n";

	return 0;
