class Hash{
public:

  ll num;
  ll idx;
  vector<ll> K;
  Int b;
  Hash(Int b):b(b){
    K.resize(N);
    K[0] = 1;
    for(Int i=1;i<N;i++) K[i] = K[i-1] * B[b] % mod[b];
    num = 0;
    idx = K[N/2];
  }

  void inc(){num = (num + idx) % mod[b];}
  void dec(){num = (num - idx + mod[b]) % mod[b];}
  void moveLeft(){idx = idx * inv[b] % mod[b];}
  void moveRight(){idx = idx * B[b] % mod[b];}

};


