    for(Int i=0;i<3;i++){
      auto &A = hash2[i];
      auto &U = allHash[i];
      auto &X = Ofset[i];
      ll val = (U - X + mod[i]) % mod[i];
      Min(mn, cnt[i][val]);
      if(ch == '+') X = (X - A.idx + mod[i]) % mod[i], A.inc();
      else if(ch == '-') X = (X + A.idx + mod[i]) % mod[i], A.dec();
      else if(ch == '>') U = U * B[i] % mod[i], A.moveRight();
      else if(ch == '<') U = U * inv[i] % mod[i], A.moveLeft();
      cnt[i][A.num]--;
    }
    ans += mn;
  }
  cout<<ans<<endl;


  return 0;
}

