typedef long long LL;
const int Mod[2] = {999988679, 999988609};
const int B[2] = {370728, 370732};
const int iB[2] = {396903752, 462741403};
const int MN = 250005;

inline void Add(int &x, int y, int i) { x -= (x += y) >= Mod[i] ? Mod[i] : 0; }

int N;
char S[MN];
int A[2][MN], D[2][MN];
std::unordered_map<LL, int> Buk;
LL Ans;

int main() {
	scanf("%d%s", &N, S + 1);
	for (int j = 0; j < 2; ++j) A[j][0] = 0, D[j][0] = 1;
	for (int i = 1, V[2] = {1, 1}; i <= N; ++i) {
		for (int j = 0; j < 2; ++j) {
			A[j][i] = A[j][i - 1];
			if (S[i] == '+') Add(A[j][i], V[j], j);
			if (S[i] == '-') Add(A[j][i], Mod[j] - V[j], j);
			if (S[i] == '>') V[j] = (LL)V[j] * B[j] % Mod[j];
			if (S[i] == '<') V[j] = (LL)V[j] * iB[j] % Mod[j];
			D[j][i] = V[j];
		}
	}
	for (int i = N; i >= 0; --i) {
		Ans += Buk[
			(A[0][i] + (LL)A[0][N] * D[0][i]) % Mod[0] +
			(A[1][i] + (LL)A[1][N] * D[1][i]) % Mod[1] * Mod[0]
		];
		++Buk[A[0][i] + A[1][i] * Mod[0]];
	}
	printf("%lld\n", Ans);
	return 0;
}

