int B[] = {0,397,823,727,401,271,571};
int InvB[10];
int f[8][MAXN],g[8][MAXN],N,h[8][MAXN];
char s[MAXN];
map<int,int> MK[8];


int mul(int a,int b) {
	return 1LL * a * b % MOD;
}
int inc(int a,int b) {
	return a + b >= MOD ? a + b - MOD : a + b;
}
void update(int &x,char c,int id) {
	if(c == '<') x = mul(x,InvB[id]);
	else if(c == '>') x = mul(x,B[id]);
	else if(c == '+') x = inc(x,1);
	else x = inc(x,MOD - 1);
}
int fpow(int x,int c) {
	int res = 1,t = x;
	while(c) {
		if(c & 1) res = mul(res,t);
		t = mul(t,t);
		c >>= 1;
	}
	return res;
}
void Solve() {
	for(int i = 1 ; i <= 6 ; ++i) InvB[i] = fpow(B[i],MOD - 2);
	read(N);
	scanf("%s",s + 1);
	int c[10] = {0};
	for(int k = 1 ; k <= 6 ; ++k) {
		for(int i = N ; i >= 1 ; --i) {
			update(c[k],s[i],k);
		}
	}
	for(int k = 1 ; k <= 6 ; ++k) h[k][N + 1] = 1,g[k][N + 1] = c[k],MK[k][0] += 1;
	int64 ans = 0;
	for(int i = N ; i >= 1 ; --i) {
		int add = N - i + 1;
		for(int k = 1 ; k <= 6 ; ++k) {
			if(s[i] == '<') {
				g[k][i] = inc(g[k][i + 1],MOD - mul(h[k][i + 1],c[k]));
				h[k][i] = mul(h[k][i + 1],B[k]);
				g[k][i] = inc(g[k][i],mul(h[k][i],c[k]));
				f[k][i] = f[k][i + 1];		
			}
			else if(s[i] == '>') {
				g[k][i] = inc(g[k][i + 1],MOD - mul(h[k][i + 1],c[k]));
				h[k][i] = mul(h[k][i + 1],InvB[k]);
				g[k][i] = inc(g[k][i],mul(h[k][i],c[k]));
				f[k][i] = f[k][i + 1];
			}
			else if(s[i] == '+') {
				h[k][i] = h[k][i + 1];
				g[k][i] = inc(g[k][i + 1],MOD - h[k][i]);
				f[k][i] = inc(f[k][i + 1],MOD - h[k][i]);
			}
			else {
				h[k][i] = h[k][i + 1];
				g[k][i] = inc(g[k][i + 1],h[k][i]);
				f[k][i] = inc(f[k][i + 1],h[k][i]);
			}
			add = min(add,MK[k][g[k][i]]);
			MK[k][f[k][i]] += 1;
		}
		ans += add;
	}
	out(ans);enter;	
}
int main() {
#ifdef ivorysi
	freopen("f1.in","r",stdin);
#endif
	Solve();
	return 0;
}

