		}
		if (arr[i] == 2)
		{
			S.fi++; nmod(S.fi, INF);
			S.se++; nmod(S.se, BIG);
		}
		if (arr[i] == 3)
		{
			S.fi--; S.fi += INF; nmod(S.fi, INF);
			S.se--; S.se += BIG; nmod(S.se, BIG);
		}
	}
	for (int i = 0; i < N; i++)
	{
		if (arr[i] == 0)
		{
			if (S == MP(0ll, 0ll)) ans++;
		}
		if (arr[i] == 1)
		{
			if (S == MP(0ll, 0ll)) ans++;
		}
		if (arr[i] == 2)
		{
			if (S == MP(1ll, 1ll)) ans++;
