void solve() {
	int n; cin >> n;
	string s; cin >> s;
	ll sum = 0; int loc = 0;
	vector<ll> memo(n);
	vector<int> locs(n);
	rep(i, n) {
		if (s[i] == '>')loc++;
		else if (s[i] == '<')loc--;
		else if (s[i] == '+') {
			if (loc >= 0) {
				sum += tr[loc];
			}
			else {
				sum += invtr[-loc];
			}
		}
		else {
			if (loc >= 0) {
				sum -= tr[loc];
			}
			else {
				sum -= invtr[-loc];
			}
		}
