inline int power(int a, int k, int MOD) {
	if (k < 0) k += MOD - 1;
	int ans = 1;
	for (; k; k >>= 1, a = (lo) a * a % MOD)
		if (k & 1) ans = (lo) ans * a % MOD;
	return ans;
}

struct val_t {
	int w1, w2;
	val_t (int w1 = 0, int w2 = 0) : w1(w1), w2(w2) {}
	inline void Set(int x, int t) {
		x += DELTA;
		(w1 += (lo) power(base1, x, MOD1) * (MOD1 + t) % MOD1) %= MOD1;
		// (w2 += (lo) power(base2, x, MOD2) * (MOD2 + t) % MOD2) %= MOD2;
	}
	val_t rot(int d) {
		val_t a = *this;
		a.w1 = (lo) a.w1 * power(base1, d, MOD1) % MOD1;
		// a.w2 = (lo) a.w2 * power(base2, d, MOD2) % MOD2;
		return a;
	}
};
val_t val[N];
map <val_t, int> mp;

