const int K = 2;
ll MODS[K] = {(ll)1e9 + 7, (ll)1e9 + 9};

ll bin_pow(ll x, ll p, ll mm) {
	if (p == 0) return 1;
	if (p & 1) return (x * bin_pow(x, p - 1, mm)) % mm;
	ll y = bin_pow(x, p / 2, mm);
	return (y * y) % mm;
}

struct PolyHash {
	ll x[K];

	PolyHash() : x() {}
	PolyHash(ll Z) {
		for (int i = 0; i < K; i++)
			x[i] = Z % MODS[i];
	}

	PolyHash operator + (const PolyHash &H) const {
		PolyHash res = PolyHash();
		for (int i = 0; i < K; i++) {
			res.x[i] = (x[i] + H.x[i]) % MODS[i];
		}
		return res;
	}

	PolyHash operator - (const PolyHash &H) const {
		PolyHash res = PolyHash();
		for (int i = 0; i < K; i++) {
			res.x[i] = (x[i] - H.x[i]) % MODS[i];
		}
		return res;
	}

	PolyHash operator * (const PolyHash &H) const {
		PolyHash res = PolyHash();
		for (int i = 0; i < K; i++) {
			res.x[i] = (x[i] * H.x[i]) % MODS[i];
		}
		return res;
	}

	bool operator < (const PolyHash &H) const {
		for (int i = 0; i < K; i++) {
			if (x[i] != H.x[i])
				return x[i] < H.x[i];
		}
		return false;
	}
};

