			else {
				sum += invtr[-loc];
			}
		}
		else {
			if (loc >= 0) {
				sum -= tr[loc];
			}
			else {
				sum -= invtr[-loc];
			}
		}
		sum = (sum%mod + mod) % mod;
		memo[i] = sum;
		locs[i] = loc;
