        for (int j = 1; j <= n * 2; j++)
            Pow[i][j] = (LL)Pow[i][j - 1] * P[i] % mod[i];
    for (int i = 1, ls = 0; i <= n; i++) {
        if (s[i] == '<') ls--;
        else if (s[i] == '>') ls++;
        else if (s[i] == '+') increase(all, ls + n);
        else decrease(all, ls + n);
    }
    Data fuck; mp[all] = 1; LL res = 0;
    for (int i = 1, ls = 0; i <= n; i++) {
        if (s[i] == '<') ls--;
        else if (s[i] == '>') ls++;
        else if (s[i] == '+') increase(fuck, ls + n);
        else decrease(fuck, ls + n);
        res += mp[fuck], mp[fuck + all * ls]++;
    }
    printf("%lld\n", res);
    return 0;
}

