		pinv[i] = mul(pinv[i - 1], inv);
	}

	cin >> n >> s;
	pref[0] = PLL(0, 0);
	pos[0] = n;
	map<PLL, int> cnt;
	for (int i = 1; i <= n; i++) {
		char t = s[i - 1];
		pos[i] = pos[i - 1];
		pref[i] = pref[i - 1];
		if (t == '<') pos[i]--;
		if (t == '>') pos[i]++;
		if (t == '+') pref[i] = add(pref[i], pw[pos[i]]);
		if (t == '-') pref[i] = add(pref[i], neg(pw[pos[i]]));
		cnt[pref[i]]++;
	}

	LL ans = 0; 
	PLL tgt = pref[n], mulf = PLL(1, 1), addf = PLL(0, 0);
	for (int i = 1; i <= n; i++) {
		ans += cnt[add(mul(get_inv(mulf), tgt), neg(mul(get_inv(mulf), addf)))];
//		cout << "At " << i << ' ' << cnt[add(mul(tgt, mulf), addf)] << '\n';

		char t = s[i - 1];
		cnt[pref[i]]--;
		if (t == '<') {
			// a *= base, b *= base
			mulf = mul(mulf, pw[1]);
			addf = mul(addf, pw[1]);
		}
		if (t == '>') {
			// a /= base, b /= base
			mulf = mul(mulf, pinv[1]);
			addf = mul(addf, pinv[1]);
		}
		if (t == '+') {
			// b -= pw[n]
			addf = add(addf, neg(pw[n]));
		}
		if (t == '-') {
			// b += pw[n]
			addf = add(addf, pw[n]);
		}
	}

	cout << ans << '\n';

	return 0;
}

