  vector<map<ll,Int> > cnt(3);
  vector<Hash> hash({Hash(1777771, 1e9+7),Hash(10000019,1000000009),Hash(10000079,1000000021)});
  vector<Hash> hash2 = hash;

  for(Int k = 0; k<n ; k++){
    char ch = s[k];
    for(Int i=0;i<1;i++){
      auto &A = hash[i];
      if(ch == '+') A.inc();
      if(ch == '-') A.dec();
      if(ch == '<') A.moveLeft();
      if(ch == '>') A.moveRight();
      cnt[i][A.num]++;
    }
  }

  vector<ll> allHash(3);
  vector<ll> Ofset(3,0);
  for(Int i=0;i<3;i++) allHash[i] = hash[i].num;

  Int ans = 0;
  for(Int k=0;k<n;k++){

    char ch = s[k];
    Int mn = INF;
    for(Int i=0;i<1;i++){
      auto &A = hash2[i];
      auto &U = allHash[i];
      auto &X = Ofset[i];

      ll mod = A.mod;
      ll B = A.B;
      ll inv = A.inv;
      ll val = ((U - X + mod)%mod) % mod;

      Min(mn, cnt[i][val]);

      if(ch == '+') X = (X - A.idx + mod) % mod, A.inc();
      if(ch == '-') X = (X + A.idx + mod) % mod, A.dec();
      if(ch == '>') U = U * B % mod, A.moveRight();
      if(ch == '<') U = U * inv % mod, A.moveLeft();
      assert(cnt[i][A.num] != 0);
      cnt[i][A.num]--;
    }
    ans += mn;
  }
  cout<<ans<<endl;


  return 0;
}

