inline int fpm(int x, int y, int MOD)
{
	int res = 1;
	for(; y; y >>= 1, x = (LL) x * x % MOD) if(y & 1) res = (LL) res * x % MOD;
	return res;
}

inline void solve()
{
	// Thanks to zhou888

	const int MOD1 = 998244353, MOD2 = 1004535809, B = 233;

	static int p[MAXN + 5];
	static int prefix1[MAXN + 5], p1_pow[MAXN * 2 + 5];
	static int prefix2[MAXN + 5], p2_pow[MAXN * 2 + 5];

	p1_pow[0 + n] = 1;
	p2_pow[0 + n] = 1;
	for(int i = 1, invB1 = fpm(B, MOD1 - 2, MOD1), invB2 = fpm(B, MOD2 - 2, MOD2); i <= n; ++i)
	{
		p1_pow[ i + n] = (LL) p1_pow[ i - 1 + n] *     B % MOD1;
		p1_pow[-i + n] = (LL) p1_pow[-i + 1 + n] * invB1 % MOD1;
		p2_pow[ i + n] = (LL) p2_pow[ i - 1 + n] *     B % MOD2;
		p2_pow[-i + n] = (LL) p2_pow[-i + 1 + n] * invB2 % MOD2;
	}

	for(int i = 1; i <= n; ++i)
	{
		p[i] = p[i - 1];
		prefix1[i] = prefix1[i - 1];
		prefix2[i] = prefix2[i - 1];
		if(s[i] == '<') --p[i];
		if(s[i] == '>') ++p[i];
		if(s[i] == '+')
		{
			(prefix1[i] += p1_pow[p[i] + n]) %= MOD1;
			(prefix2[i] += p2_pow[p[i] + n]) %= MOD2;
		}
		if(s[i] == '-')
		{
			(prefix1[i] -= p1_pow[p[i] + n]) %= MOD1;
			(prefix2[i] -= p2_pow[p[i] + n]) %= MOD2;
		}
	}
	for(int i = 1; i <= n; ++i)
	{
		(prefix1[i] += MOD1) %= MOD1;
		(prefix2[i] += MOD2) %= MOD2;
	}

	static map<pii, int> cnt;

	LL ans = 0;
	for(int i = 1; i <= n; ++i) ans += prefix1[i] == prefix1[n] && prefix2[i] == prefix2[n];
	for(int i = 1; i <= n; ++i)
	{
		ans += cnt[mp(prefix1[i], prefix2[i])];
		int res1 = (prefix1[i] + (LL) p1_pow[p[i] + n] * prefix1[n] % MOD1) % MOD1;
		int res2 = (prefix2[i] + (LL) p2_pow[p[i] + n] * prefix2[n] % MOD2) % MOD2;
		++cnt[mp(res1, res2)];
	}
	printf("%lld\n", ans);
}

int main()
{
