ll bp(ll a, ll p) {
    ll r = 1;
    while (p > 0) {
        if (p & 1)
            r = r * a % MOD;
        a = a * a % MOD;
        p >>= 1;
    }
    return r;
}

ll inv_X = bp(X, MOD - 2);

ll a[N], b[N];

int main() {
