            h = h * X % MOD;
        else if (s[i] == '<')
            h = h * inv_X % MOD;
        else if (s[i] == '+')
            h = (h + 1) % MOD;
        else
            h = (h - 1 + MOD) % MOD;
    }

    map<ll, int> mp;

    ll ans = 0;

