	u32 a[K], b[K];
	Linear operator()(const Linear &f) {
		Linear g;
		for(int i=0; i<K; ++i) g.a[i]=(u64)a[i]*f.a[i]%P;
		for(int i=0; i<K; ++i) g.b[i]=((u64)a[i]*f.b[i]+b[i])%P;
		return g;
	}
	bool operator==(const Linear &o) {
		return memcmp(b, o.b, sizeof(b))==0;
	}
	u32 hash() {
		u32 sum=0;
		for(int i=0; i<K; ++i) sum=(sum*65536llu+b[i])%Ha;
