        for (int i = n - 1; i >= 0; --i) {
            switch (s[i]) {
                case '+' :
                    c = (c + 1) % P;
                    break;
                case '-':
                    c = (c - 1 + P) % P;
                    break;
                case '>':
                    c = 1LL * c * BASE[j] % P;
                    break;
                case '<':
                    c = 1LL * c * INV_BASE[j] % P;
                    break;
            }
        }
        auto calc = [&](vector<vector<int>> &a, int c) {
            a[n][j] = c;
            int k = 1, b = 0;
