                switch (s[i]) {
                    case '+' :
                        b = (b - k + P) % P;
                        break;
                    case '-':
                        b = (b + k) % P;
                        break;
                    case '>':
                        k = 1LL * k * INV_BASE[j] % P;
                        break;
                    case '<':
                        k = 1LL * k * BASE[j] % P;
                        break;
                }
                a[i][j] = (1LL * k * c + b) % P;
            }
        };
        calc(a, c);
        calc(b, 0);
    }
    LL ans = 0;
    map<vector<int>, int> cnt;
    for (int i = 0; i <= n; ++i) {
        ans += cnt[b[i]];
        ++cnt[a[i]];
    }
    cout << ans << endl;
    return 0;
}

