const int mod = 1e9 + 9, base = 500005;
int n, pwbase[base], hashs[base], cur = 250000, ans, start[base];
string s;
map<int, int> cnt;

int binpw (int aa, int mm) {
    int ret = 1;
    for (; mm; mm >>= 1, aa = (1LL * aa * aa) % mod) if (mm & 1) ret = (1LL * ret * aa) % mod;
    return ret;
}

int gethash (int i, int j) { return ( (hashs[j] - hashs[i - 1]) % mod + mod) % mod; }

signed main () {
    ios_base::sync_with_stdio(0); cin.tie(0); cout.tie(0);
    cin >> n >> s; s = '*' + s;
    pwbase[0] = 1;
    for (int i = 1; i < base; ++i) pwbase[i] = (pwbase[i - 1] * base) % mod;
    for (int i = 1; i <= n; ++i) {
        start[i] = cur;
        if(s[i] == '<') cur --;
        else if(s[i] == '>') cur ++;
        hashs[i] = hashs[i - 1];
        if (s[i] == '+') hashs[i] = (hashs[i] + pwbase[cur]) % mod;
        else if (s[i] == '-') hashs[i] = ( (hashs[i] - pwbase[cur]) % mod + mod) % mod;
    }
    int X = (gethash(1, n) * binpw(pwbase[ start[1] ], mod - 2) ) % mod;

    for (int i = n; i > 0; --i) {
        cnt[ hashs[i] ] ++;
        ans += cnt[X * pwbase[ start[i] ] + hashs[i - 1] ];
    }

    cout << ans;

    return 0;
}

