struct Hash {
  vector<int> data;
  Hash(int x) {
    data.resize(hashes.size(), 0);
    rep(i, hashes.size()) data[i] = ((x%hashes[i])+hashes[i])%hashes[i];
  }
  Hash() : Hash(0) {}
};
Hash operator+(const Hash &a, const Hash &b) {
  Hash ret;
  rep(i, hashes.size()) ret.data[i] = (0LL+a.data[i]+b.data[i]) % hashes[i];
  return ret;
}
Hash operator*(Hash a, Hash b) {
  rep(i, hashes.size()) a.data[i] = (1LL*a.data[i]*b.data[i]) % hashes[i];
  return a;
}
Hash inv(Hash a) {
  Hash ret;
  rep(i, hashes.size()) ret.data[i] = modpow(a.data[i], hashes[i]-2, hashes[i]);
  return ret;
}


