inline void Add(int& x,int y,int mod) {
  x = x + y >= mod ? x + y - mod : x + y;
}
inline void Sub(int& x,int y,int mod) {
  x = x - y < 0 ? x - y + mod : x - y;
}
int power(int a,int b,int mod) {
  int ret = 1;
  while (b) {
    if (b&1) ret = 1ll * ret * a % mod;
    a = 1ll * a * a % mod;
    b >>= 1;
  }
  return ret;
}

char str[N];
int n, rec[2], val[2][N][2], tmp[2][N][2];
ll ans;
map<pii,int> mp;
void solve(int l,int r) {
  if (l == r) {
    if (val[1][l][0] == rec[0] && val[1][l][1] == rec[1])
      ++ ans;
    return;
  }
  int mid = (l + r) >> 1;
  mp.clear();
  rep (k, 0, 1) {
    tmp[0][mid][k] = val[0][mid][k], tmp[1][mid][k] = val[1][mid][k];
    rrp (i, mid-1, l) {
      tmp[0][i][k] = 1ll * val[0][i][k] * tmp[0][i+1][k] % MOD[k];
      tmp[1][i][k] = (1ll * val[0][i][k] * tmp[1][i+1][k] + val[1][i][k]) % MOD[k];
    }
    tmp[0][mid+1][k] = val[0][mid+1][k], tmp[1][mid+1][k] = val[1][mid+1][k];
    rep (i, mid+2, r) {
      tmp[0][i][k] = 1ll * tmp[0][i-1][k] * val[0][i][k] % MOD[k];
      tmp[1][i][k] = (1ll * tmp[0][i-1][k] * val[1][i][k] + tmp[1][i-1][k]) % MOD[k];
    }
  }
  rep (i, mid+1, r) ++ mp[pii(tmp[1][i][0], tmp[1][i][1])];
  rep (i, l, mid) {
    // tmp[0][i] * v + tmp[1][i] = rec
    int v0 = rec[0];
    Sub(v0, tmp[1][i][0], MOD[0]);
    v0 = 1ll * v0 * power(tmp[0][i][0], MOD[0] - 2, MOD[0]) % MOD[0];
    int v1 = rec[1];
    Sub(v1, tmp[1][i][1], MOD[1]);
    v1 = 1ll * v1 * power(tmp[0][i][1], MOD[1] - 2, MOD[1]) % MOD[1];
    ans += mp[pii(v0, v1)];
  }
  solve(l, mid);
  solve(mid+1, r);
}
