        if(S[i] == '<') f[i + 1]--;
    }

    mod[0] = 1e9 + 7;
    mod[1] = 1e9 + 9;
    mod[2] = 1e9 + 21;
    mod[3] = 1e9 + 33;

    process(0);
    process(1);
    process(2);
    process(3);

    mp[pii({H[n][0],H[n][1]},{H[n][2],H[n][3]})] = 1;

    int ans = 0;

    for(int i = 1 ; i <= n ; ++i)   {
        ans += mp[pii({H[i][0],H[i][1]},{H[i][2],H[i][3]})];
        int res0 = H[n][0] * qpow(N,f[i],mod[0]) + H[i][0];
        int res1 = H[n][1] * qpow(N,f[i],mod[1]) + H[i][1];
        int res2 = H[n][2] * qpow(N,f[i],mod[2]) + H[i][2];
        int res3 = H[n][3] * qpow(N,f[i],mod[3]) + H[i][3];
        res0 %= mod[0]; res1 %= mod[1];
        res2 %= mod[2]; res3 %= mod[3];
        mp[pii({res0,res1},{res2,res3})]++;
    }

    cout << ans << endl;
}

