void update(int &id,int il,int ir,int pos)
{
    if (id==-1)
        id=newnode();
    if (ir-il==1)
    {
        ++num[id];
        return;
    }
    int mid=(il+ir)>>1;
    if (pos<mid)
        update(sl[id],il,mid,pos);
    else
        update(sr[id],mid,ir,pos);
    ++num[id];
}
int query(int id,int il,int ir,int pos)
{
    if (id==-1)
        return 0;
    if (ir-il==1)
        return num[id];
    int mid=(il+ir)>>1;
    return pos<mid?query(sl[id],il,mid,pos):query(sr[id],mid,ir,pos);
}
