struct hash{
  int mo,ni,f[N],g[N];
  int fap(int a,int b){
    int s=1;
    for (; b; b>>=1,a=1ll*a*a%mo)
      if (b&1) s=1ll*s*a%mo;
    return s;
  }
  void solve(int x,char c){
    if (c=='+')
      g[x]=g[x-1],f[x]=(f[x-1]+g[x])%mo;
    else if (c=='-')
      g[x]=g[x-1],f[x]=(f[x-1]+mo-g[x])%mo;
    else if (c=='<')
      f[x]=f[x-1],g[x]=1ll*g[x-1]*ni%mo;
    else
      f[x]=f[x-1],g[x]=1ll*g[x-1]*bas%mo;
    return;
  }
  int calc(int x){
    return (1ll*g[x]*f[n]+f[x])%mo;
  }
}T[5];
