	ll ans = 0;
	vector<unordered_map<ll, int>> mp(len);
	rep(i, len) {
		mp[i][alls[i]]++;
	}
	rep(i, n) {
		//cout << mp[memo[i]] << endl;
		int mi = 2*n;
		rep(j, len) {
			mi = min(mi, mp[j][memo[i][j]]);
		}
		ans += mi;
		bool valid = true;

		rep(j, len) {
			ll nex = alls[j];
			ll mod = mods[j / roots.size()];
			if (locs[i] >= 0) {
				nex = nex * tr[locs[i]][j] % mod;
			}
			else {
				nex = nex * invtr[-locs[i]][j] % mod;
			}
			nex += memo[i][j];
			if (nex >= mod)nex -= mod;
			mp[j][nex]++;
		}
	}
	cout << ans << endl;
}


