long long int ppow(long long int i, long long int j, long long int MOD) {
	long long int ret = 1;
	while (j) {
		if (j & 1LL)ret *= i;
		if (ret >= MOD)ret %= MOD;
		j >>= 1LL;
	}
	return ret;
}

valarray<long long int> calc(int dif) {
	valarray<long long int> ret;
	ret.resize(2, 0);
	for (int j = 0; j < 2; j++) {
		if (dif < 0) {
			ret[j]=ppow(ppow(MOD1[j],MOD2[j]-2,MOD2[j]),-dif,MOD2[j]);
		}
		else {
			ret[j] = ppow(MOD1[j], abs(dif), MOD2[j]);
		}
	}
	return ret;
}

int main() {
	MOD1.resize(2);
	MOD2.resize(2);
	p2.push_back(valarray<long long int>());
