			hs += MOD2;
		}
		hs %= MOD2;
		vv.push_back(hs);
		ps.push_back(pos);
	}
	long long int way = 0;
	for (int i = vv.size() - 1; i >= 0; i--) {
		valarray<long long int> want = vv[i] + vv.back()*calc(-MAX/2+ps[i]);
