using Data = tuple<mll, ll, mll>;

Data prod(Data a, Data b){
	mll ha; ll pa; mll qpa; tie(ha, pa, qpa) = a;
	mll hb; ll pb; mll qpb; tie(hb, pb, qpb) = b;
	return tuple(ha+qpa*hb, pa+pb, qpa*qpb);
}

void solve()
{
	ll N; string S;  cin >> N >> S;
	mll q = 999983;
	mll qinv = mll(1) / q;

	Data plus  = { 1, 0, 1};
	Data minus = {-1, 0, 1};
	Data right = { 0, 1, q};
	Data left  = { 0,-1, qinv};

	//W
	Data W ={0,0,1};
	DEPS(i, 0, N-1){
		Data si;
		if      (S[i] == '+') si = plus;
		else if (S[i] == '-') si = minus;
		else if (S[i] == '>') si = right;
		else                  si = left;
		W = prod(si, W);
	}
	mll hW = get<0>(W);

	unordered_map<ll, ll> CinvMap;
	Data X ={0,0,1};
	Data Xinv ={0,0,1};
	ll ans = 0;
	DEPS(i, 0, N-1){
		//XinvCinv
		ll h = get<0>(Xinv).val;
		CinvMap[h]++;
		//X
		Data si;
		if      (S[i] == '+') si = plus;
		else if (S[i] == '-') si = minus;
		else if (S[i] == '>') si = right;
		else                  si = left;
		X = prod(si, X);
		//Xinv
		Data siinv;
		if      (S[i] == '+') siinv = minus;
		else if (S[i] == '-') siinv = plus;
		else if (S[i] == '>') siinv = left;
		else                  siinv = right;
		Xinv = prod(Xinv, siinv);

		//C^-1h
		mll hX = get<0>(X);
		mll qpXinv = get<2>(Xinv);
		mll hCinv = (hW-hX) * qpXinv;
		if (CinvMap.count(hCinv.val) == 1){
			ans += CinvMap[hCinv.val];
		}
	}
	cout << ans << '\n';
}


int main(){
	solve();
	return 0;
}

