const int mod0 = 1e9 + 9, mod1 = 1e9 + 7, base = 500005;
int n, pwbase[2][base], hashs[2][base], cur = 250000, ans, start[base];
string s;
map< pair<int, int>, int> cnt;

int binpw (int aa, int mm, int mod) {
    int ret = 1;
    for (; mm; mm >>= 1, aa = (1LL * aa * aa) % mod) if (mm & 1) ret = (1LL * ret * aa) % mod;
    return ret;
}

int gethash (bool type, int i, int j) {
    if(!type) return ( (hashs[type][j] - hashs[type][i - 1]) % mod0 + mod0) % mod0;
    else return ( (hashs[type][j] - hashs[type][i - 1]) % mod1 + mod1) % mod1;
}

signed main () {
    ios_base::sync_with_stdio(0); cin.tie(0); cout.tie(0);
    cin >> n >> s; s = '*' + s;
    pwbase[0][0] = pwbase[1][0] = 1;
    for (int i = 1; i < base; ++i) {
        pwbase[0][i] = (pwbase[0][i - 1] * base) % mod0;
        pwbase[1][i] = (pwbase[1][i - 1] * base) % mod1;
    }
    for (int i = 1; i <= n; ++i) {
        start[i] = cur;
        if(s[i] == '<') cur --;
        else if(s[i] == '>') cur ++;
        hashs[0][i] = hashs[0][i - 1];
        hashs[1][i] = hashs[1][i - 1];
        if (s[i] == '+') {
            hashs[0][i] = (hashs[0][i] + pwbase[0][cur]) % mod0;
            hashs[1][i] = (hashs[1][i] + pwbase[1][cur]) % mod1;
        }
        else if (s[i] == '-') {
            hashs[0][i] = ( (hashs[0][i] - pwbase[0][cur]) % mod0 + mod0) % mod0;
            hashs[1][i] = ( (hashs[1][i] - pwbase[1][cur]) % mod1 + mod1) % mod1;
        }
    }
    pair<int, int> X = { (gethash(0, 1, n) * binpw(pwbase[0][ start[1] ], mod0 - 2, mod0) ) % mod0,
                        (gethash(1, 1, n) * binpw(pwbase[1][ start[1] ], mod1 - 2, mod1)) % mod1 };

    for (int i = n; i > 0; --i) {
        cnt[ { hashs[0][i], hashs[1][i] } ] ++;
        ans += cnt[ { X.fi * pwbase[0][ start[i] ] + hashs[0][i - 1], X.se * pwbase[1][ start[i] ] + hashs[1][i - 1] } ];
    }

    cout << ans;

    return 0;
}

