		if (st[i]=='-')
		{
			a[nowp]--;
			qz[i]=(qz[i]-thn[nowp-1]+mod2)%mod2;
		}
		if (st[i]=='>') nowp++;
		if (st[i]=='<') nowp--;
		where[i]=nowp;
	}
	a_hash=0;
