		}
		long long target=(a_hash-qz[0]+mod2)%mod2;
		if (where[i-1]<where[0])
			target=target*ksmi(thn[abs(where[i-1]-where[0])],mod2-2,mod2)%mod2;
		else	target=target*thn[abs(where[0]-where[i-1])]%mod2;
		node s;
		s.x=p1[i];s.y=qz[i];
