            a = (a * rK) % MOD;
            a2 = (a2 * rK2) % MOD;
        }
    }
    if (s[0] == '+') pref0[0] = (pref0[0] + 1) % MOD;
    if (s[0] == '+') pref02[0] = (pref02[0] + 1) % MOD;
    if (s[0] == '-') pref0[0] = (pref0[0] + MOD - 1) % MOD;
    if (s[0] == '-') pref02[0] = (pref02[0] + MOD - 1) % MOD;
    if (s[0] == '<') pref0[0] = (pref0[0] * K) % MOD;
    if (s[0] == '<') pref02[0] = (pref02[0] * K2) % MOD;
    if (s[0] == '>') pref0[0] = (pref0[0] * rK) % MOD;
    if (s[0] == '>') pref02[0] = (pref02[0] * rK2) % MOD;
    if (s[0] == '>') moves[0]++;
    if (s[0] == '<') moves[0]--;
    for (int i = 1; i < n; i++)
    {
        if (s[i] == '+') pref0[i] = (pref0[i - 1] + 1) % MOD;
        if (s[i] == '+') pref02[i] = (pref02[i - 1] + 1) % MOD;
        if (s[i] == '-') pref0[i] = (pref0[i - 1] + MOD - 1) % MOD;
        if (s[i] == '-') pref02[i] = (pref02[i - 1] + MOD - 1) % MOD;
        if (s[i] == '<') pref0[i] = (pref0[i - 1] * K) % MOD;
        if (s[i] == '<') pref02[i] = (pref02[i - 1] * K2) % MOD;
        if (s[i] == '>') pref0[i] = (pref0[i - 1] * rK) % MOD;
        if (s[i] == '>') pref02[i] = (pref02[i - 1] * rK2) % MOD;
        moves[i] = moves[i - 1];
        if (s[i] == '>') moves[i]++;
        if (s[i] == '<') moves[i]--;
    }
    for (int j = 0; j < n; j++)
    {
        if (s[j] == '+')
        {
            suf0[0] = (suf0[0] + 1) % MOD;
            suf02[0] = (suf02[0] + 1) % MOD;
        }
        if (s[j] == '-')
        {
            suf0[0] = (suf0[0] + MOD - 1) % MOD;
            suf02[0] = (suf02[0] + MOD - 1) % MOD;
        }
        if (s[j] == '<')
        {
            suf0[0] = (suf0[0] * K) % MOD;
            suf02[0] = (suf02[0] * K2) % MOD;
        }
        if (s[j] == '>')
        {
            suf0[0] = (suf0[0] * rK) % MOD;
            suf02[0] = (suf02[0] * rK2) % MOD;
        }
    }
    if (a != 0 && a2 != 0) return 1;
    for (int i = 0; i < n; i++) suf0[i + 1] = (suf0[0] - pref0[i] * deg[moves[i]] + MOD * MOD) % MOD;
    for (int i = 0; i < n; i++) suf02[i + 1] = (suf02[0] - pref02[i] * deg2[moves[i]] + MOD * MOD) % MOD;
    for (int i = 0; i < n; i++) sufa[i] = (suf0[i] + a * deg[(i > 0 ? moves[i - 1] : 0) - moves[n - 1]]) % MOD;
    for (int i = 0; i < n; i++) sufa2[i] = (suf02[i] + a2 * deg2[(i > 0 ? moves[i - 1] : 0) - moves[n - 1]]) % MOD;
    ll ans = 0;
    map<vector<ll>, ll> cnt;
    for (int i = n; i >= 0; i--)
    {
        ans += cnt[{sufa[i], sufa2[i]}];
        cnt[{suf0[i], suf02[i]}]++;
    }
    cout << ans;
}

