int p[N];

void add(int &x, int y) {
	x += y;
	if (x >= mod)
		x -= mod;
	if (x < 0) 
		x += mod;
}

int mult(int x, int y) {
	return x * 1ll * y % mod;
}

int hsh[N], pos[N];

pair<int, int> get(int l, int r) {
	return mp(hsh[r + 1], pos[r + 1]);
}

int inv(int x) {
	int pw = mod - 2;
	int ans = 1;
	while (pw) {
		if (pw & 1) 
			ans = mult(ans, x);
		x = mult(x, x);
		pw /= 2;
	}
	return ans;
}

// get(l,r)=(get(0,r)-get(0,l-1))<<pos(0, l-1)

int shift(int x, int val) { // to the right
	if (val > 0) {
		x = mult(x, p[val]);
	} else {
		x = mult(x, inv(p[-val]));
	}
	return x;
}

int sum(int x, int y) {
	add(x, y);
	return x;
}

