			add(hsh[i + 1], -p[pos[i + 1]]);
		} else if (st[i] == '>') {
			pos[i + 1]++;
		} else {
			pos[i + 1]--;
		}
	}
	int x = get(0, n - 1).f;
	int ans = 0;
	map<int, int> cnt;
	for (int i = 0; i < n; i++) {
