typedef long long LL;

struct MyInt64 {
	LL X;
	MyInt64(LL X = 0LL): X(X) {}
};

const LL Base = 1926081719260847LL, BaseInv = 696411651951639283LL, Mod = 999999999999999989LL;

inline bool operator < (MyInt64 P1, MyInt64 P2) {
	return P1.X < P2.X;
}

inline MyInt64 operator + (MyInt64 P1, MyInt64 P2) {

	P1.X += P2.X;
	if(P1.X >= Mod)
		P1.X -= Mod;

	return P1;
}

inline MyInt64 operator - (MyInt64 P1, MyInt64 P2) {

	P1.X -= P2.X;
	if(P1.X < 0)
		P1.X += Mod;

	return P1;
}

inline MyInt64& operator += (MyInt64&P1, MyInt64 P2) {

	P1.X += P2.X;
	if(P1.X >= Mod)
		P1.X -= Mod;

	return P1;
}

inline MyInt64 operator * (MyInt64 P1, MyInt64 P2) {

	MyInt64 Ans;

	while(P2.X) {

		if(P2.X & 1)
			Ans += P1.X;

		P2.X >>= 1;
		P1.X <<= 1;
		if(P1.X >= Mod)
			P1.X -= Mod;

	}

	return Ans;
}

