using namespace std;

typedef vector<long long> Vec;

long long mods[] = {1000000007, 100000007, 1000000009};
long long bs[] = {1000003, 1000033, 1000033};
long long is[3];

Vec ad(Vec v1, Vec v2, int coe){
	for(int i = 0; i < 3; ++i) v1[i] += (coe * v2[i]);
	for(int i = 0; i < 3; ++i){
		v1[i] %= mods[i];
		if(v1[i] < 0) v1[i] += mods[i];
	}
	return v1;
}

long long ex(long long a, long long e, long long m){
	if(e == 0) return 1;
	long long tmp = ex(a, e / 2, m);
	tmp = (tmp * tmp) % m;
	if(e % 2 == 1) tmp *= a;
	tmp %= m;
	return tmp;
}

void init(){
	for(int i = 0; i < 3; ++i){
		is[i] = ex(bs[i], mods[i] - 2, mods[i]);
	}
}

Vec cur;

int N;
char ch[250200];

void input(){
	scanf("%d", &N);
	scanf("%s", ch);
}

int ps[250200];

Vec vs[250200];

map<vector<long long>, vector<int> > mp;

Vec mv(Vec v, int e){
	if(e == 0) return v;
	for(int i = 0; i < 3; ++i){
		long long a = (e > 0) ? (bs[i]) : (is[i]);
		v[i] *= ex(a, e > 0 ? e : (-e), mods[i]);
		v[i] %= mods[i];
		if(v[i] < 0) v[i] += mods[i];
	}
	return v;
}

long long solve(){
	init();
	Vec v(3);
	ps[0] = 0;
	mp[v].push_back(0);
	for(int i = 0; i < 3; ++i){
		cur.push_back(1);
	}
	vs[0] = v;
	for(int i = 0; i < N; ++i){
		if(ch[i] == '>'){
			ps[i + 1] = ps[i] + 1;
			cur = mv(cur, 1);
		}else if(ch[i] == '<'){
			ps[i + 1] = ps[i] - 1;
			cur = mv(cur, -1);
		}else if(ch[i] == '+'){
			ps[i + 1] = ps[i];
			v = ad(v, cur, 1);
		}else{
			ps[i + 1] = ps[i];
			v = ad(v, cur, -1);
		}
		for(int j = 0; j < 3; ++j){
			v[j] %= mods[j];
			if(v[j] < 0) v[j] += mods[j];
		}
		mp[v].push_back(i + 1);
		vs[i + 1] = v;
	}
/*	printf("----\n");
	for(int i = 0; i < 3; ++i){
		printf("%lld ", v[i]);
	}
	printf("\n");*/
	long long ans = 0;
	for(int i = 0; i < N; ++i){
		Vec tmp = mv(vs[N], ps[i]);
		Vec v = ad(vs[i], tmp, 1);
		if(mp.count(v) == 0) continue;
		vector<int> &vec = mp[v];
		int id = distance(vec.begin(), lower_bound(vec.begin(), vec.end(), i + 1));
		ans += (vec.size() - id);
	/*	for(int j = id; j < vec.size(); ++j){
			printf("%d %d\n", i, vec[j]);
		}*/
	}
	return ans;
}

int main(){
