		if (c == '-') h0 = (h0 - pow_mod(345678, x, MOD0) + MOD0) % MOD0;
		if (c == '+') h1 = (h1 + pow_mod(345678, x, MOD1)) % MOD1;
		if (c == '-') h1 = (h1 - pow_mod(345678, x, MOD1) + MOD1) % MOD1;
		if (c == '>') x++;
		if (c == '<') x--;
		X[i + 1] = x, H0[i + 1] = h0, H1[i + 1] = h1;
