		if (dx >= 0) coef1 = pow_mod(345678, dx, MOD1);
		else coef1 = inv_mod(pow_mod(345678, -dx, MOD1), MOD1);
		ll target0 = (H0[i] + H0[N] * coef0) % MOD0;
		ll target1 = (H1[i] + H1[N] * coef1) % MOD1;
		ans += mp[{target0, target1}];
		mp[{H0[i], H1[i]}]++;
	}
	cout << ans << endl;
}

