const LL HMOD = 998244353, HRAD = 3, HIRAD = (HMOD + 1) / HRAD;
map<LL, int> S;
int n;
char ch[250005];
LL tot[250005];

int main()
{
	scanf("%d%s", &n, &ch);
	LL cur = 1LL, cnt = 0LL;
	rep(i, n) {
		if(ch[i] == '+') cnt = (cnt + cur) % HMOD;
		if(ch[i] == '-') cnt = (cnt - cur + HMOD) % HMOD;
		if(ch[i] == '<') cur = cur * HIRAD % HMOD;
		if(ch[i] == '>') cur = cur * HRAD % HMOD;
		tot[i] = cnt;
		S[cnt] ++;
	}
	LL ss = cnt, ans = S[ss];
	cur = 1LL; cnt = 0LL;
	rep(i, n) {
		if(ch[i] == '+') cnt = (cnt + cur) % HMOD;
		if(ch[i] == '-') cnt = (cnt - cur + HMOD) % HMOD;
		if(ch[i] == '<') cur = cur * HIRAD % HMOD;
		if(ch[i] == '>') cur = cur * HRAD % HMOD;
		S[tot[i]] --;
		ans += S[(cnt + cur * ss) % HMOD];
	}
	printf("%lld\n", ans);
	return 0;
}

