		if(str[i] == '<') pos--;
		if(str[i] == '+') Sum += Pw[pos];
		if(str[i] == '-') Sum -= Pw[pos];
		Pos[i] = pos,Val[i] = Sum;
	}

	Map[Val[n] * Pw[n + 5]]++;
	ll ans = 0;
