size_t Pow(size_t x, int n){
    if(n < 0) n = mod - 1 + n;
    size_t ret = 1, pv = x;
    for(;n;n>>=1){
        if(n & 1) ret = 1LL * ret * pv % mod;
        pv = 1LL * pv * pv % mod;
    }
    return ret;
}

