ll bp(ll a, ll p) {
    ll r = 1;
    while (p > 0) {
        if (p & 1) {
            r = r * a % MOD;
        }
        a = a * a % MOD;
        p >>= 1;
    }
    return r;
}

ll inv_X = bp(X, MOD - 2);

ll a[N], b[N];

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    int n;
    string s;
    cin >> n >> s;

    ll h = 0;

    for (int i = n - 1; i >= 0; i--) {
        if (s[i] == '>')
            h = h * X % MOD;
        else if (s[i] == '<')
            h = h * inv_X % MOD;
        else if (s[i] == '+')
            h = (h + 1) % MOD;
        else
            h = (h - 1 + MOD) % MOD;
    }

    map<ll, int> mp;

    ll ans = 0;

    a[n] = 1;

    for (int i = n - 1; i >= 0; i--) {
        mp[b[i + 1]]++;

        a[i] = a[i + 1];
        b[i] = b[i + 1];

