            a[i] = a[i + 1] * X % MOD;
            b[i] = b[i + 1];
        }
        else if (s[i] == '+') {
            a[i] = a[i + 1];
            b[i] = (b[i + 1] - a[i + 1] + MOD) % MOD;
        }
        else {
            a[i] = a[i + 1];
            b[i] = (b[i + 1] + a[i + 1]) % MOD;
        }

        // cout << b[i] << "\n";

        ans += mp[(a[i] * h + b[i]) % MOD];
    }

    // cout << (a[0] * h + b[0]) % MOD << "\n";

